				$('#banner300x250r14').attr("id", "dotnAd-300x250-r16x");
				dotnAdShowAdAsync("300x250", "r16");
			}
		}
	}


	// Remap per compatibilità
	$('#banner300x250r15').attr("id", "dotnAd-300x250-r15");
	//$('#banner300x100casa').attr("id", "dotnAd-300x100-casa");
	$('#banner970x250').attr("id", "dotnAd-970x250-middle");

	if (!isMobileDevice && $( "#dotnAd-970x250-middle" ).length) // annulla lazy per piedone
		dotnAdShowAdAsync("970x250", "middle");

	var isHomePage = ($('meta[property="og:title"]').attr("content").indexOf('ANSA.it - Homepage') != -1) ? true : false;
	var isNews = ((document.location.href.indexOf("/notizie/") != -1) && !isHP()) ? true : false;
	var inReadDone = false;

	$(document).ready(function() {
		jQuery.scrollDepth({
			//minHeight: 2000,
			//elements: ['#comments', 'footer'],
			elements : [/*'#dotnAd-300x250-r15', '#dotnAd-300x100-casa', '#dotnAd-970x250-middle', '#dotnAd-300x250-r16',*/ '#inRead'],
			percentage : false,
			//userTiming: false,
			pixelDepth : false,
			//nonInteraction: false
			eventHandler : function(data) {
				//$('#dotnAd-300x250-r15').html('<script type="text\/javascript">if (!isMobileDevice) dotnAdShowAdAsync("300x250", "r15")<\/script>');
				if (!isMobileDevice) {
					//if (data['eventLabel'].indexOf('r14')!= -1) dotnAdShowAdAsync("300x250", "r14");
					//if (data['eventLabel'].indexOf('r15') != -1)
						//dotnAdShowAdAsync("300x250", "r15");
					//if (data['eventLabel'].indexOf('casa') != -1)
						//dotnAdShowAdAsync("300x100", "casa");
					if (data['eventLabel'].indexOf('middle') != -1)
						dotnAdShowAdAsync("970x250", "middle");
				} else {
					if (data['eventLabel'].indexOf('r16') != -1 && false) // disabilitato il banner lazy, togliere false per riabilitarlo e rivedere il codice sopra)
						if ($(window).width() < 768)
							dotnAdShowAdAsync("300x250", "r16");
				}
				if (!isMobileDevice && !inReadDone && (isHomePage || isNews) && !withinCMS()) {
					if (data['eventLabel'].indexOf('inRead') != -1) {
						//console.log(data['eventLabel']);
						//isNews ? displayVideoAd('#inReadVideo', 468, 263) : displayVideoAd('#inReadVideo', 416, 234);
						isNews ? displayVideoAd('#inReadVideo', 300, 168) : displayVideoAd('#inReadVideo', 416, 234);
						//$("#inReadVideo").slideDown(2000);
						//showInRead();
						inReadDone = true;
					}
				}

			}
		});

		$(window).scroll(function() {// bind window scroll event
			if ($('#inRead').length > 0) {// if target element exists in DOM
				if ($('#inRead').visible()) {// if target element is visible on screen after DOM loaded
					inreadResume();
				} else {
					inreadPause();
				}
			}
		});

	});
}

var	pageTitle = document.title;
var	pageUrl = window.location.href;
//Parameters for the Popup window
var winWidth 	= 650;	
var winHeight	= 450;
var winLeft   	= ($(window).width()  - winWidth)  / 2;
var winTop    	= ($(window).height() - winHeight) / 2;	
var winOptions   = 'width='  + winWidth  + ',height=' + winHeight + ',top=' + winTop + ',left='   + winLeft;

//user clicks on a share button
$('.button-wrap').click(function(event) {

		var shareName = $(this).attr('class').split(' ')[0]; //get the first class name of clicked element

		switch (shareName) //switch to different links based on different social name
		{
			case 'facebook':
				var openLink = 'https://www.facebook.com/sharer/sharer.php?u=' + encodeURIComponent(pageUrl) + '&amp;title=' + encodeURIComponent(pageTitle);
				break;
			case 'twitter':
				//var openLink = 'http://twitter.com/home?status=' + encodeURIComponent(pageTitle + ' ' + pageUrl);
				var openLink = '//twitter.com/intent/tweet?text=' + encodeURIComponent(pageTitle + ' ' + pageUrl);
				break;
			case 'digg':
				var openLink = 'http://www.digg.com/submit?phase=2&amp;url=' + encodeURIComponent(pageUrl) + '&amp;title=' + encodeURIComponent(pageTitle);
				break;
			case 'stumbleupon':
				var openLink = 'http://www.stumbleupon.com/submit?url=' + encodeURIComponent(pageUrl) + '&amp;title=' + encodeURIComponent(pageTitle);
				break;
			case 'delicious':
				var openLink = 'http://del.icio.us/post?url=' + encodeURIComponent(pageUrl) + '&amp;title=' + encodeURIComponent(pageTitle);
				break;
			case 'google':
				var openLink = 'https://plus.google.com/share?url=' + encodeURIComponent(pageUrl) + '&amp;title=' + encodeURIComponent(pageTitle);
				break;
			case 'email':
				var openLink = 'mailto:?subject=' + pageTitle + '&body=Ti suggerisco questo link: ' + pageUrl;
				break;
			case 'linkedin':
				var openLink = 'http://www.linkedin.com/shareArticle?mini=true&url='+encodeURIComponent(pageUrl)+'&amp;title='+encodeURIComponent(pageTitle)+'&summary=&source=ansa.it';
				break;
			case 'pinterest':
				var openLink = 'http://pinterest.com/pin/create/button/?url='+encodeURIComponent(pageUrl)+'&amp;description='+encodeURIComponent(pageTitle);
				break;
		}

	//open Popup window and redirect user to share website.
	window.open(openLink,'_blank',winOptions);
	$('#share-div').hide();
	return false;
});

window.onload = function(e) {
		if (document.location.href.indexOf('refresh_ce')!=-1 && !(document.hasFocus()))
			googletag.pubads().refresh();
}
